import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header/Header';
import CourseList from './components/CourseList/CourseList';
import Planner from './components/Planner/Planner';
import LoginPage from './components/LoginPage/LoginPage';
import './App.css';

// Main App component with Router
function App() {
  return (
    <div className="App">
      <Router>
        <AppRoutes />
      </Router>
    </div>
  );
}

// Separate component for handling routes and navigation
function AppRoutes() {
  const [isAuthenticated, setIsAuthenticated] = useState(() => {
    const storedAuth = localStorage.getItem('isAuthenticated');
    const expiryTime = localStorage.getItem('expiryTime');
    const currentTime = new Date().getTime();
    return storedAuth === 'true' && expiryTime && currentTime < expiryTime;
  });

  const navigate = useNavigate(); // Hook to programmatically navigate

  const handleLogin = () => {
    const expiryTime = new Date().getTime() + 30 * 60 * 1000; // 30 minutes from now
    localStorage.setItem('isAuthenticated', 'true');
    localStorage.setItem('expiryTime', expiryTime);
    setIsAuthenticated(true);
    navigate('/'); // Navigate to home or the default authenticated route
  };

  const handleLogout = () => {
    localStorage.removeItem('isAuthenticated');
    localStorage.removeItem('expiryTime');
    setIsAuthenticated(false);
    navigate('/login'); // Navigate to login page
  };

  useEffect(() => {
    const checkSession = () => {
      const storedAuth = localStorage.getItem('isAuthenticated');
      const expiryTime = localStorage.getItem('expiryTime');
      const currentTime = new Date().getTime();
      if (storedAuth === 'true' && expiryTime && currentTime >= expiryTime) {
        handleLogout();
      }
    };

    const handleUserActivity = () => {
      // Reset the session timeout
      const newExpiryTime = new Date().getTime() + 30 * 60 * 1000; // 30 minutes from now
      localStorage.setItem('expiryTime', newExpiryTime);
    };

    checkSession();

    // Set up event listeners for user activity
    window.addEventListener('mousemove', handleUserActivity);
    window.addEventListener('keydown', handleUserActivity);
    window.addEventListener('scroll', handleUserActivity);

    // Clean up event listeners on unmount
    return () => {
      window.removeEventListener('mousemove', handleUserActivity);
      window.removeEventListener('keydown', handleUserActivity);
      window.removeEventListener('scroll', handleUserActivity);
    };
  }, [isAuthenticated]);

  return (
    <>
      {isAuthenticated ? (
        <>
          <Header onLogout={handleLogout} />
          <Routes>
            <Route path="/" element={<CourseList />} />
            <Route path="/courses" element={<CourseList />} />
            <Route path="/planner" element={<Planner />} />
          </Routes>
        </>
      ) : (
        <Routes>
          <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
          {/* Add a fallback route to always redirect to login if not authenticated */}
          <Route path="*" element={<LoginPage onLogin={handleLogin} />} />
        </Routes>
      )}
    </>
  );
}

export default App;
