import React from 'react'
import { Draggable } from 'react-beautiful-dnd'
import styles from './DraggableItem.module.css'

export default function DraggableItem({ course, index }) {
  return (
    // <Draggable key={course.courseNr} draggableId={course.courseNr} index={index}>
    <div>
      {(provided) => (
        <div
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          className={styles.courseItem}
        >
          <div className={styles.dragableCourse}></div>
          <div className={styles.courseContent}>{course.name}</div>
        </div>
      )}
      </div>
    // </Draggable>
  )
}
