import React, { useState } from 'react'
import styles from './Planner.module.css'
import Dashboard from './Dashboard/Dashboard'
import Schedule from './Schedule/Schedule'
import { DragDropContext } from 'react-beautiful-dnd'

const initialDegreeRequirements = [
  {
    name: 'The polytechnic foundation',
    nrOfCourses: 9,
    nrOfCompletedCourses: 4,
    color: 'rgb(33 208 130)',
    courses: [
      { courseNr: '01003', name: 'Mathematics 1a (Polytechnical Foundation)', ects: 10 },
      { courseNr: '02003', name: 'Computer Programming', ects: 5 },
      { courseNr: '26020', name: 'Fundamental Chemistry', ects: 5 },
      { courseNr: '10063', name: 'Physics (Polytechnical Foundation)', ects: 5 },
      { courseNr: '01004', name: 'Mathematics 1b (Polytechnical Foundation)', ects: 10 },
      { courseNr: '02402', name: 'Statistics (Polytechnical Foundation)', ects: 5 },
      { courseNr: '27020', name: 'Bioengineering (Polytechnical Foundation)', ects: 5 },
      { courseNr: '42620', name: 'Science, Technology and Society (Polytechnical Foundation)', ects: 5 },
    ]
  },
  {
    name: 'Projects',
    nrOfCourses: 4,
    nrOfCompletedCourses: 2,
    color: '#e84048',
    courses: [
      { courseNr: '27016', name: 'Design-Build 1: Devices for Measuring Cell Growth', ects: 5 },
      { courseNr: '10240', name: 'Design-Build 2: Rational Catalyst Design', ects: 5 },
      { courseNr: '47203', name: 'Design-Build 3: Solar Cooker', ects: 5 },
      { courseNr: '22400', name: 'Design-build 4: Autonomous devices for controlling and studying living systems', ects: 5 },
    ]
  },
]

const initialContainers = [
  { id: 'container1', items: [] },
  { id: 'container2', items: [] },
]

function Planner() {
  const [lists, setLists] = useState(initialDegreeRequirements)
  const [containers, setContainers] = useState(initialContainers)

  const onDragEnd = (result) => {
    const { source, destination } = result

    if (!destination) {
      return
    }

    if (source.droppableId === destination.droppableId && source.index === destination.index) {
      return
    }

    const sourceList = source.droppableId.startsWith('list')
      ? lists.find(list => list.name === source.droppableId)
      : containers.find(container => container.id === source.droppableId)

    const destinationList = destination.droppableId.startsWith('list')
      ? lists.find(list => list.name === destination.droppableId)
      : containers.find(container => container.id === destination.droppableId)

    const [movedItem] = sourceList.courses.splice(source.index, 1)
    destinationList.items.splice(destination.index, 0, movedItem)

    if (source.droppableId.startsWith('list')) {
      setLists(lists.map(list => (list.name === source.droppableId ? sourceList : list)))
    } else {
      setContainers(containers.map(container => (container.id === source.droppableId ? sourceList : container)))
    }

    if (destination.droppableId.startsWith('list')) {
      setLists(lists.map(list => (list.name === destination.droppableId ? destinationList : list)))
    } else {
      setContainers(containers.map(container => (container.id === destination.droppableId ? destinationList : container)))
    }
  }

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className={styles.planner}>
        <Dashboard lists={lists} setLists={setLists} />
        <Schedule containers={containers} setContainers={setContainers} />
      </div>
    </DragDropContext>
  )
}

export default Planner
