import React, { useState, useContext } from 'react'

import styles from './BottomBar.module.css'

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronDown, faChevronUp, faPlus } from '@fortawesome/free-solid-svg-icons'

import CourseNavItem from './CourseNavItem/CourseNavItem'
import { PlansContext } from '../Planner'

export default function BottomBar() {
    const { selectedPlanID, courseInfoList } = useContext(PlansContext)

    const [dropdownOpen, setDropdownOpen] = useState(false)

    return (
        <div className={styles.bottomBar}>
            <div className={styles.navBar}>
                {courseInfoList.map(courseNr => (
                    <CourseNavItem courseNr='02003' />

                ))}
                <CourseNavItem courseNr='01003' />
            </div>

            <div className={styles.header}>
                <div className={styles.headerTitle}>Design-build 4: Autonomous devices for controlling and studying living systems</div>
                {dropdownOpen ? (
                    <FontAwesomeIcon icon={faChevronUp} className={styles.caretIcon}/>
                ) : (
                    <FontAwesomeIcon icon={faChevronDown} className={styles.caretIcon}/>
                )}
            </div>

            <div className={styles.content}>
                <div className={styles.courseInfo}>

                </div>

                <div className={styles.courseDescription}>

                </div>
            </div>
        </div>
    )
}
