import React, { useState, useContext } from "react"

import SemesterItem from "./SemesterItem/SemesterItem"
import { PlansContext } from "../Planner"
import { findPlanByID } from './../utils'

import { Droppable, Draggable } from 'react-beautiful-dnd'

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlus } from '@fortawesome/free-solid-svg-icons' 

import styles from './Schedule.module.css'

export default function Schedule() {
    const { selectedPlanID } = useContext(PlansContext)

    const plan = findPlanByID(selectedPlanID)

    const handleAddSemesterClick = () => {

    }


    useEffect(() => {
        const adjustHeight = () => {
            // Logic to calculate and set the height
            // For example, reducing height if a bottom div is visible
            const bottomDivHeight = 50; // Assume bottom div height is 50px
            setScheduleHeight(`calc(100% - ${bottomDivHeight}px)`);
        }

        // Call the function initially or whenever a condition changes
        adjustHeight();

        // You can add dependencies to the useEffect if it should re-run on certain state changes
    }, [/* dependencies like bottom div visibility */]);



    return (
        <div className={styles.scheduleView}>
            <button
                className={styles.addSemester}
                onClick={() => handleAddSemesterClick()}
            >
                <FontAwesomeIcon icon={faPlus} className={styles.plusIcon}/>
                <span>New Semester</span>
            </button>

            <div className={styles.semesterWrapper}>
                {plan.schedule.map((semester, index) => (
                    <SemesterItem semester={semester} /> 
                ))}

            </div>

        </div>
    )
}
