import plans from './degreeCourses.json'

export const extractPlanNames = () => {
    return plans.map(plan => plan.planName)
}

const findPlanByID = (planID) => {
    return plans.find(plan => plan.planID === planID)
}

export const countTotalGroupsByPlanID = (planID) => {
    const plan = plans.find(plan => plan.planID === planID)
    if (plan) {
      return plan.dashboardCourses.length
    }
    return 0
}

export const countTotalCoursesByPlanID = (planID) => {
    const plan = plans.find(plan => plan.planID === planID)
    if (plan) {
      return plan.dashboardCourses.reduce((count, category) => count + category.courses.length, 0)
    }
    return 0
}

export const countTotalScheduleCoursesByPlanID = (planID) => {
    const plan = plans.find(plan => plan.planID === planID)
    if (plan) {
      return plan.schedule.reduce((total, term) => total + term.coursesNr.length, 0)
    }
    return 0
}
