import React, { useState } from "react"

import styles from './ContentDashboard.module.css'

export default function ContentDashboard(props) {
    const [dropdownOpen, setDropdownOpen] = useState(false)

    if(props.completedCourses == 0) {
        const progressPercentage = 3
    } else if (props.completedCourses == props.totalCourses) {
        const progressPercentage = 4
    } else {
        const progressPercentage = (props.completedCourses / props.totalCourses) * 100;
    }

    return (
        <div className={styles.mainView}>
            <h1>Degree Requirements</h1>
            <div className={styles.degreeName}>
                <span>BSc. General Engineering</span>
            </div>
            <div className={styles.progress}>
                <div className={styles.progressBar}>
                    <div 
                        className={styles.myProgress}
                        style={{ width: `${progressPercentage}%` }}
                    ></div>
                </div>
                <span></span>
            </div>
        </div>
    )
}
