import React, { useState } from "react";
import { Droppable, Draggable } from 'react-beautiful-dnd';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCaretDown, faCaretUp } from '@fortawesome/free-solid-svg-icons';
import styles from './ContentDashboard.module.css';

export default function ContentDashboard({ lists, setLists }) {
  const [dropdownsOpen, setDropdownsOpen] = useState(Array(lists.length + 1).fill(false));

  const handleDropdownClick = (index) => {
    setDropdownsOpen(dropdownsOpen.map((open, i) => (i === index ? !open : open)));
  };

  return (
    <div className={styles.mainView}>
      <h1>Degree Requirements</h1>
      <div className={styles.degreeName}>
        <span>BSc. General Engineering</span>
      </div>

      <div className={styles.requirements}>
        <div 
          className={styles.showRequirements}
          onClick={() => handleDropdownClick(0)}
        >
          {dropdownsOpen[0] ? (
            <FontAwesomeIcon icon={faCaretUp} className={styles.caretIcon} />
          ) : (
            <FontAwesomeIcon icon={faCaretDown} className={styles.caretIcon} />
          )}

          {dropdownsOpen[0] ? (
            <span>Hide All Degree Requirements</span>
          ) : (
            <span>View All Degree Requirements</span>
          )}
        </div>

        {dropdownsOpen[0] && (
          <div className={styles.allRequirements}>
            {lists.map((list, index) => (
              <div key={list.name} className={styles.requirementItem}>
                <div className={styles.smallLine}></div>
                <div 
                  className={styles.showRequirement}
                  onClick={() => handleDropdownClick(index + 1)}
                >
                  {dropdownsOpen[index + 1] ? (
                    <FontAwesomeIcon icon={faCaretUp} className={styles.innerCaretIcon} />
                  ) : (
                    <FontAwesomeIcon icon={faCaretDown} className={styles.innerCaretIcon} />
                  )}
                  <div>
                    <span>{list.name}</span>
                    <span>{list.nrOfCompletedCourses}/{list.nrOfCourses}</span>
                  </div>
                </div>

                <Droppable droppableId={list.name}>
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.droppableProps}
                      className={styles.requirementCourses}
                    >
                      {dropdownsOpen[index + 1] && list.courses.map((course, courseIndex) => (
                        <Draggable key={course.courseNr} draggableId={course.courseNr} index={courseIndex}>
                          {(provided) => (
                            <div
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                              className={styles.courseItem}
                            >
                              <div className={styles.dragableCourse}></div>
                              <div className={styles.courseContent}>{course.name}</div>
                            </div>
                          )}
                        </Draggable>
                      ))}
                      {provided.placeholder}
                    </div>
                  )}
                </Droppable>
              </div>
            ))}
          </div>
        )}

        <div className={styles.line}></div>
      </div>
    </div>
  );
}
