import { useContext } from 'react'

import { PlansContext } from '../../Planner'

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faXmark } from '@fortawesome/free-solid-svg-icons'

import styles from './CourseNavItem.module.css'

export default function CourseNavItem({ courseNr, courseColor }) {

    const { selectedPlanID, courseInfoList, selectedCourseInfoNr } = useContext(PlansContext)

    return (
        <div 
            className={styles.courseItem} 
            style={{ 
                backgroundColor: courseColor,
                border: selectedCourseInfoNr === courseNr ? '2px solid #000' : 'none'
            }}
            // Add an if statement here and if true it should modify the border
        >
            <div className={styles.title}>{courseNr}</div>
            <FontAwesomeIcon icon={faXmark} className={styles.xIcon}/>
        </div>
    )
}
