import React, { createContext, useState } from 'react'
import styles from './Planner.module.css'
import Dashboard from './Dashboard/Dashboard'
import Schedule from './Schedule/Schedule'
import plans from './degreeCourses.json' // Import the JSON data

// Define the context
export const DegreeCoursesContext = createContext()

export default function Planner() {
  const [selectedPlanID, setSelectedPlanID] = useState("1") // Default to the first plan

  const value = {
    plans: plans,
    selectedPlanID,
    setSelectedPlanID,
  }

  return (
    <DegreeCoursesContext.Provider value={value}>
      <div className={styles.planner}>
        <Dashboard />
        <Schedule />
      </div>
    </DegreeCoursesContext.Provider>
  )
}
