import plans from './degreeCourses.json'

export const extractPlanNames = (plans) => {
    return plans.map(plan => plan.planName)
}

export const countTotalCourses = (plans) => {
    return plans.reduce((total, plan) => {
      const coursesCount = plan.dashboardCourses.reduce((count, category) => count + category.courses.length, 0)
      return total + coursesCount
    }, 0)
}

export const countTotalGroupsByPlanID = (planID) => {
    const plan = plans.find(plan => plan.planID === planID)
    if (plan) {
      return plan.dashboardCourses.length
    }
    return 0
}
